import React, {
  useState,
  useEffect
} from 'react';
import {
  useNavigate
} from 'react-router-dom';
import '../../css/order.css';

function Order() {
  const [orderData, setOrderData] = useState({
    pickupLocation: '',
    dropOffLocation: '',
    packageDetails: '',
    deliveryTime: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    const email = localStorage.getItem('email');
    const username = localStorage.getItem('username');

    // Check if user credentials are present
    if (!userId || !email || !username) {
      alert('Please log in first');
      navigate('/login'); // Redirect to login page
    } else {
      // Validate user ID against the server
      const validateUser = async () => {
        try {
          const response = await fetch('http://localhost:3000/validate-user', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              userId
            }),
          });

          if (!response.ok) {
            alert('Your account has been deleted or is no longer valid. Please log in again.');
            navigate('/login'); // Redirect to login page
          }
        } catch (error) {
          console.error('Error validating user:', error);
          alert('An error occurred while validating your account. Please log in again.');
          navigate('/login');
        }
      };

      validateUser();
    }
  }, [navigate]);


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/create-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderData),
      });
      if (response.ok) {
        alert('Order created successfully');
        navigate('../welcome.js');
      } else {
        alert('Failed to create order');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred. Please try again.');
    }
  };

  return ( <
    div className = "auth-wrapper" >
    <
    div className = "auth-inner order-inner" >
    <
    h2 > Create Order < /h2> <
    form onSubmit = {
      handleSubmit
    } >
    <
    div className = "form-group" >
    <
    label > Pickup Location < /label> <
    input type = "text"
    className = "form-control"
    value = {
      orderData.pickupLocation
    }
    onChange = {
      (e) => setOrderData({
        ...orderData,
        pickupLocation: e.target.value
      })
    }
    required /
    >
    <
    /div> <
    div className = "form-group" >
    <
    label > Drop - off Location < /label> <
    input type = "text"
    className = "form-control"
    value = {
      orderData.dropOffLocation
    }
    onChange = {
      (e) => setOrderData({
        ...orderData,
        dropOffLocation: e.target.value
      })
    }
    required /
    >
    <
    /div> <
    div className = "form-group" >
    <
    label > Package Details < /label> <
    textarea className = "form-control"
    value = {
      orderData.packageDetails
    }
    onChange = {
      (e) => setOrderData({
        ...orderData,
        packageDetails: e.target.value
      })
    }
    required /
    >
    <
    /div> <
    div className = "form-group" >
    <
    label > Delivery Time < /label> <
    input type = "datetime-local"
    className = "form-control"
    value = {
      orderData.deliveryTime
    }
    onChange = {
      (e) => setOrderData({
        ...orderData,
        deliveryTime: e.target.value
      })
    }
    required /
    >
    <
    /div> <
    button type = "submit"
    className = "btn btn-gradient btn-block" > Submit Order < /button> <
    /form> <
    /div> <
    /div>
  );
}

export default Order;
