import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
// import '../../css/userOrders.css'; // Custom CSS for styling (create this if needed)
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

function UserOrders() {
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      alert('Please log in first');
      navigate('/login');
    } else {
      const fetchUserOrders = async () => {
        try {
          const response = await fetch(`http://localhost:3000/user-orders/${userId}`);
          if (response.ok) {
            const data = await response.json();
            setOrders(data);
          } else {
            console.error('Failed to fetch orders');
          }
        } catch (error) {
          console.error('Error fetching orders:', error);
        }
      };
      fetchUserOrders();
    }
  }, [navigate]);

  return (
    
    <div className="auth-wrapper">
      <div className="auth-inner">
        <h2>Your Orders</h2>
        {orders.length > 0 ? (
          <div className="order-list">
            {orders.map((order) => (
              <div key={order.id} className="order-card">
                <div className="order-details">
                  <p><strong>Pickup Location:</strong> {order.pickup_location}</p>
                  <p><strong>Drop-off Location:</strong> {order.drop_off_location}</p>
                  <p><strong>Package Details:</strong> {order.package_details}</p>
                  <p><strong>Delivery Time:</strong> {new Date(order.delivery_time).toLocaleString()}</p>
                  <p><strong>Status:</strong> {order.status || 'Pending'}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default UserOrders;
